package com.chinaredstar.videoplayer;import android.content.ComponentCallbacks2;import android.content.res.Configuration;/** * Created by chen.huarong on 2019-04-23. * 视频播放提供给外部的回调 */public class VideoPlayerCallBack implements ComponentCallbacks2 {    /**     * 开始播放     */    public void onStart() {    }    /**     * 准备好播放     */    public void onPrepared() {    }    /**     * 暂停     */    public void onPause() {    }    /**     * 重新播放，暂停、播放出错、播放完成状态下重新播放     */    public void onRestart() {    }    /**     * 停止播放     */    public void onStop() {    }    /**     * 释放surface、mediaplayer、audiomanager     */    public void onReleasePlayer() {    }    /**     * 释放播放器     */    public void onRelease() {    }    /**     * 视频准备旋转     *     * @param mode {@link IVideoPlayer#MODE_FULL_SCREEN,IVideoPlayer#MODE_NORMAL}     */    @Deprecated    public void onVideoPlayerRotationPrepare(int mode) {    }    /**     * 视频旋转完成     *     * @param mode {@link IVideoPlayer#MODE_FULL_SCREEN,IVideoPlayer#MODE_NORMAL}     */    @Deprecated    public void onVideoPlayerRotationFinished(int mode) {    }    @Override    public void onTrimMemory(int level) {    }    @Override    public void onConfigurationChanged(Configuration newConfig) {    }    @Override    public void onLowMemory() {    }}